using System.ComponentModel.Composition;
using XrmToolBox.Extensibility;
using XrmToolBox.Extensibility.Interfaces;


namespace PZone.XrmToolBox
{
    [Export(typeof(IXrmToolBoxPlugin))]
    [ExportMetadata("Name", "Everywhere Search")]
    [ExportMetadata("Description", "Global search for various objects throughout the system.")]
    [ExportMetadata("SmallImageBase64", "")] // A base64 string that represents a 32x32 pixels wide image
    [ExportMetadata("BigImageBase64", "")] // A base64 string that represents a 80x80 pixels wide image
    [ExportMetadata("BackgroundColor", "Lavender")] // Background color of the plugins item in the plugins list
    [ExportMetadata("PrimaryFontColor", "Black")] // Font color for Name, Description, Version (and Author in Large icons display mode)
    [ExportMetadata("SecondaryFontColor", "Gray")] // Font color for Author in Small icons display mode
    public class EverywhereSearch : PluginBase
    {
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new EverywhereSearchControl();
        }
    }
}